import React, { useState, useEffect } from 'react'
import {Col,Row,Container,ListGroup,ListGroupItem} from 'react-bootstrap'
import axios from 'axios'
function ProductsScreen({match}) {
    const [data, setData] = useState({});
    const [loading,setLoading] = useState(true);
    useEffect(() => {
        const fetchData = async () => {
            try {
                const res = await axios.get(`/api/products/${match.params.id}`)
                setData(res.data[0])
                setLoading(false)
            } catch (err) {
                throw new Error(err.message)
            }
        }
        fetchData();
    }, [match.params.id]);
    if(loading){
       return( <></>)
    }
    return (
        <>
        <Container>
           <Row style={{marginTop:'40px'}}>
               <Col md={6} lg={6} sm={12}><img src={data.image} alt={data.name} style={{width:"100%"}}></img>
                           </Col>
               <Col md={3} lg={3} sm={12}>
               <ListGroup variant="flush">
  <ListGroup.Item as="h1"> {data.name}</ListGroup.Item>
  <ListGroup.Item>
      <span className="rate"><i className={`${data.rating>=0.5?"fas fa-star":data.rating>0?"fas fa-star-half-alt":"far fa-star"}`}></i></span>
      <span className="rate"><i className={`${data.rating>=1.5?"fas fa-star":data.rating>1?"fas fa-star-half-alt":"far fa-star"}`}></i></span>
      <span className="rate"><i className={`${data.rating>=2.5?"fas fa-star":data.rating>2?"fas fa-star-half-alt":"far fa-star"}`}></i></span>
      <span className="rate"><i className={`${data.rating>=3.5?"fas fa-star":data.rating>3?"fas fa-star-half-alt":"far fa-star"}`}></i></span>
      <span className="rate"><i className={`${data.rating>=4.5?"fas fa-star":data.rating>4?"fas fa-star-half-alt":"far fa-star"}`}></i></span>
      <span className="review">{`  `}{data.numReviews} reviews</span>
  </ListGroup.Item>
  <ListGroup.Item>Price : {data.price} $</ListGroup.Item>
  <ListGroup.Item>description : {data.description}</ListGroup.Item>
</ListGroup>
               </Col>

               <Col md={3} lg={3} sm={12}>
               <ListGroup>
  <ListGroup.Item>
  <Row>
<Col>Price</Col>
<Col>{data.price}</Col>
  </Row>
  </ListGroup.Item>
  <ListGroup.Item>
  <Row>
<Col>status</Col>
<Col>{data.price}</Col>
  </Row>
  </ListGroup.Item>
  <ListGroup.Item>Morbi leo risus</ListGroup.Item>

</ListGroup>
               </Col>
           </Row>
           </Container>
        </>
    )
}

export default ProductsScreen
