import React, { useState, useEffect } from 'react'
import { Row, Col, Container } from 'react-bootstrap'
import Product from "../Components/product.js"
import {useDispatch,useSelector} from 'react-redux'
import {ProductListAction} from '../Actions/ProductListAction'
const HomeScreen = () => {
    const [data, setData] = useState([]);
    const [loading,setLoading] = useState(true);
    useEffect(() => {
        const fetchData = async () => {
            try {
                const res = await axios.get('/api/products')
                setData(res.data)
                setLoading(false)
            } catch (err) {
                throw new Error(err.message)
            }
        }
        fetchData();
    }, []);
    if(loading){
       return( <h1>Loading</h1>)
    }
    return (<>
        <marquee direction="right" className="marq">Featured pdoducts</marquee>
        <Container>
        <Row style={{}}>

        {data.map((d)=>{
            return(<Col key={d._id} md={6} xl={3} lg={4} style={{justifyContent:'center',display:'flex',margin:'10px 0'}}>
               <Product id={d._id} ></Product>
               </Col>)
         })}

        </Row>
        </Container>
    </>)
}

export default HomeScreen
